import React from "react";

function AwardModal({ isOpen, onClose, award }) {
    const imagePath = require(`../../assets/images/${award.image}`);
    return (
        <div className={`fixed inset-0 flex justify-center items-center ${isOpen ? '' : 'hidden'}`}>
            <div className="bg-white dark:bg-gray-700 rounded-lg shadow p-4 md:p-5 text-center max-w-md w-full">
                <p className="mb-5 text-lg font-semibold">You just earned an achievement!</p>
                <img src={imagePath} alt={award.name} className="h-32 mx-auto"/>
                <h2 className="font-bold text-3xl">{award.name}</h2>
                <p className="text-gray-500 text-sm mt-2 mb-5">{award.description}</p>
                <button onClick={onClose} type="button" className="custom-btn w-full">Continue</button>
            </div>
        </div>
    );
}

export default AwardModal;
