import React from "react";
import FreshStart from '../../assets/images/achievement-first-time-user.svg';
import MilestoneMastered from '../../assets/images/achievement-milestone-masterted.svg';
import BeyondBasics from '../../assets/images/achievement-beyond-basics.svg';
import ChallengeAccepted from '../../assets/images/achievement-challenge-accepted.svg';
import Mastermind from '../../assets/images/achievement-mastermind.svg';

function AwardModal({ isOpen, onClose, award }) {
    return (
        <div tabIndex="-1" className={`${isOpen ? '' : 'hidden'} fixed top-0 right-0 left-0 z-50 justify-center items-center w-full md:inset-0 h-[calc(100%-1rem)] max-h-full`}>
            <div className="relative p-4 w-full max-w-md max-h-full">
                <div className="relative bg-white rounded-lg shadow dark:bg-gray-700">
                    <div className="p-4 md:p-5 text-center">
                        <p className="mb-5 text-lg text-semibold">You just earned an achievement!</p>
                        <img src={BeyondBasics} alt={award.name} className="h-32 inline-block"/>
                        <h2 className="font-bold text-3xl">{award.name}</h2>
                        <p className="text-gray-500 text-sm mt-2 mb-5">{award.description}</p>
                        <button onClick={onClose} type="button" className="custom-btn w-full">Continue</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AwardModal;
