import React, { useState, useEffect } from "react";
import Success from '../../assets/images/success.svg';

function Alert ({ message }) {
    const [isVisible, setIsVisible] = useState(true);
  
    useEffect(() => {
    //   const timeout = setTimeout(() => setIsVisible(false), 5000);
  
    //   return () => clearTimeout(timeout);
    }, []);
  
    return (
        isVisible && (
            <div className="alert-container fixed top-1 left-0 w-1/3 h-12 bg-green-500 text-white flex items-center justify-center z-50">
                <img src={Success} alt="Alert Success" className="size-10" />
                <span>{message}</span>
            </div>
        )
    );
};

export default Alert;
