import React, { useState, useEffect } from "react";
import Close from '../../assets/images/close.svg';
import QueryPerson from '../../assets/images/person.svg';
import { DndContext } from '@dnd-kit/core';
import Droppable from './Droppable';
import Draggable from './Draggable';
import Correct from '../../assets/images/correct.svg';
import Wrong from '../../assets/images/wrong.svg';

function Level({ levelData, onSubmit }) {
    const [queryItems, setQueryItems] = useState([]);
    const [keywords, setKeywords] = useState(['Select', 'from', 'AND', 'OR', '*', '`customers`']);
    const [results, setResults] = useState({ data: [] });
    const [correct, setCorrect] = useState(null);
    const [levels, setLevels] = useState([]);
    const [currentLevelIndex, setCurrentLevelIndex] = useState(0);

    const query = 'Select * from `customers`';

    const addKeywordToQuery = (e) => {
        const newKeyword = e.active.data.current?.title;
        if (e.over?.id !== 'droppable' || !newKeyword) return;
        const temp = [...queryItems];
        temp.push(newKeyword);
        setQueryItems(temp);

        const updatedKeywords = keywords.filter((keyword) => keyword !== newKeyword);
        setKeywords(updatedKeywords);
    };

    const handleCheckButtonClick = async(event) => {
        const queryString = queryItems.join(' ');
        if (queryString === query) {
            event.target.innerText = event.target.innerText === 'Check' ? 'Continue' : 'Check';
            setCorrect(true);
            const response = await fetch('/api/query', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ query: queryString }),
            });
      
            const data = await response.json();
            setResults(data);
        } else {
            setCorrect(false);
        }
   };

    useEffect(() => {
        console.log(results);
    }, [results]);

    const uniqueColumns = new Set(results.data.flatMap((row) => Object.keys(row)));

    useEffect(() => {
        const fetchLevels = async () => {
            const queryString = 'Select * from levels';
            try {
                const response = await fetch('/api/levels', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ query: queryString }),
                });
                const levelData = await response.json();
                setLevels(levelData);
                console.log(levels);
                
            } catch (error) {
                console.error('Error fetching levels:', error);
            }
        };
        fetchLevels();
    }, [levels]);

    const handleNextLevel = () => {
        const currentLevel = levels[currentLevelIndex];
    }

    if (loading) {
        return <div>Loading...</div>; // Render a loading indicator while fetching levels
    }

    return (
        <main className="relative h-screen">
            <a href="home.html" className="absolute top-0 left-0 m-4"><img src={Close} alt="close" className="w-7"/></a>
            {levels.length > 0 && (
                <section className="flex p-5">
                    <div className="w-1/2 px-8 mt-6">
                        <div className="w-full bg-gray-200 rounded-full h-4 my-4">
                            <div className="bg-blue-600 h-4 rounded-full w-1/3"></div>
                        </div>                
                        <h1 className='font-bold text-2xl'>Select All</h1>
                        <p className='text-gray-500'> Imagine you have a friend's contact list. To see all their contacts, you'd say, "Hey, can you show me ALL the information in your 'contacts' list?" In SQL, to get all data from a table named "customers," you'd use the SELECT * symbol.</p>
                        <img src={QueryPerson} alt="person" className="h-40 inline-block mt-4"/>
                        <span className="speech-bubble ms-4 rounded-lg text-lg">Display all data from a table named "customers"</span>
                        <DndContext onDragEnd={addKeywordToQuery}>
                            <Droppable items={queryItems} setQueryItems={setQueryItems} queryItems={queryItems} setKeywords={setKeywords} keywords={keywords} />
                            <ul>
                                {keywords.map((keyword) => (
                                    <Draggable key={keyword}>{keyword}</Draggable>
                                ))}
                            </ul>
                        </DndContext>
                    </div>
                    <div className="w-1/2 px-8 mt-6">
                        <div className="relative overflow-x-auto">
                            {results.data?.length > 0 && (
                                <table className="w-full text-sm text-left rtl:text-right text-gray-500">
                                    <thead className="text-xs text-gray-700 uppercase bg-gray-200">
                                        <tr>
                                            {Array.from(uniqueColumns).map((key) => (
                                                <th key={key} scope="col" className="px-6 py-3">
                                                    {key}
                                                </th>
                                            ))}
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {results.data.map((result, index) => (
                                            <tr key={index}>
                                            {Object.keys(result).map((key) => (
                                                <td key={key} className="px-6 py-4">
                                                {result[key]}
                                                </td>
                                            ))}
                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            )}
                        </div>
                    </div>
                </section>
            )}
            {correct === null && (
                <section className="flex p-5 justify-end bg-gray-200 absolute bottom-0 w-full h-40 items-center">
                    <button className="custom-btn me-8" onClick={handleCheckButtonClick}>Check</button>
                </section>
            )}

            {correct === true && (
                <section className="flex p-5 justify-between bg-green-400 absolute bottom-0 w-full h-40 items-center">
                    <div className='flex items-center gap-3'>
                        <img src={Correct} alt='correct' className='w-32' />
                        <h2 className='text-2xl font-bold text-white'>That's correct! Good Job.</h2>
                    </div>
                    <button className="custom-btn me-8" onClick={handleNextLevel}>Continue</button>
                </section>
            )}

            {correct === false && (
                <section className="flex p-5 justify-between bg-secondary absolute bottom-0 w-full h-40 items-center">
                    <div className='flex items-center gap-3'>
                        <img src={Wrong} alt='correct' className='w-32' />
                        <div>
                            <h2 className='text-2xl font-bold text-white'> Wrong! This is the correct query: </h2>
                            <span className='text-white font-semibold text-lg'>{query}</span>
                        </div>
                    </div>
                    <button className="custom-btn me-8">Continue</button>
                </section>
            )}
        </main>
    ); 
}

export default Level;
