import React, { useContext, useEffect, useState } from 'react';
import LevelItem from './LevelItem';
import RightArrow from '../../assets/images/arrow-right.svg';
import { DashboardContext } from '../context/DashboardContext';

function LevelBanner({ title, description, levelItems, level }) {
    const [dashboard] = useContext(DashboardContext); // Access currentLevel from context or prop drilling

    return (
        <>
        <div className={`relative ${level} text-white w-2/3 rounded-lg px-5 py-4 mx-auto mt-7`}>
          <h2 className='font-bold text-3xl text-white'>{title}</h2>
          <p className='font-semibold text-blue-100'>{description}</p>
          <a href='play.html' className='absolute right-4 bottom-3 size-10'>
            <img src={RightArrow} alt='continue learning' />
          </a>
        </div>
        <ul className="mt-5 text-center">
          {levelItems.map((level) => (
            <LevelItem key={level.id} level={level} currentLevel={dashboard.currentLevel} />
          ))}
        </ul>
      </>
    );
}

export default LevelBanner;
