import React, {useState} from 'react';

function Login() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [usernameError, setUsernameError] = useState(null);
    const [passwordError, setPasswordError] = useState(null);
    const [errorMessage, setErrorMessage] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('/api/login', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ username, password }),
            });

            const data = await response.json();

            if (data.message === 'Login successful!') {
                console.log("login success");
            } else {
                setErrorMessage(data.message);
                console.log(data.message);
            }
        } catch (err) {
            console.error(err);
            setErrorMessage('Internal server error!');
        }
    };

    return (
        <section>
            <form className="max-w-md mx-auto my-24" onSubmit={handleSubmit}>
                <h1 className='font-bold text-4xl'>Welcome Back!</h1>
                <p className='text-gray-500 text-sm mt-2'>Don't have an account? <a href='' className='text-primary font-semibold'>Register here</a></p>
                <ul className='mt-5'>
                    <li className="mb-5">
                        <label htmlFor="username" className="block mb-2 font-medium">Your username</label>
                        <input 
                            type="text"
                            name='username' 
                            id="username" 
                            className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" 
                            value={username} 
                            onChange={(e) => setUsername(e.target.value)}
                            placeholder='Enter your username here' 
                        />
                        {usernameError && <p className="error-message">{usernameError}</p>}
                    </li>
                    <li className="mb-2 relative">
                        <label htmlFor="password" className="block mb-2 font-medium">Your password</label>
                        <div className="relative">
                            <input 
                                type="password" 
                                id="password" 
                                className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5 pr-10" 
                                value={password} 
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder='Enter your password here' 
                            />
                            {passwordError && <p className="error-message">{passwordError}</p>}
                            <span className="absolute top-2 right-0 flex items-center pr-3">
                                <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="w-6 h-6 stroke-gray-600">
                                    <path strokeLinecap="round" strokeLinejoin="round" d="M2.036 12.322a1.012 1.012 0 0 1 0-.639C3.423 7.51 7.36 4.5 12 4.5c4.638 0 8.573 3.007 9.963 7.178.07.207.07.431 0 .639C20.577 16.49 16.64 19.5 12 19.5c-4.638 0-8.573-3.007-9.963-7.178Z" />
                                    <path strokeLinecap="round" strokeLinejoin="round" d="M15 12a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
                                </svg>
                            </span>
                        </div>
                    </li>
                    <li className="">
                        <a href='' className='text-sm text-primary float-end'>Forgot password?</a>
                    </li>
                </ul>
                <button type="submit" className="custom-btn w-full mt-5">Login</button>            
            </form>
        </section>
    );
}

export default Login;
