import React, { useState, useEffect } from 'react';
import Send from '../../assets/images/send.svg';
import DashboardNav from './UserDashboardNav';

function Community() {
    const [help, setHelp] = useState('');
    const [posts, setPosts] = useState([]);
    const [reply, setReply] = useState('');
    const [replies, setReplies] = useState({});

    const handlePostSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('authToken');
        try {
            const response = await fetch('/api/post', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ token, help }),
            });
            const data = await response.json();    
            if (data.message === 'Post successful!') {
                console.log("successful");
                setHelp('');
            } else {
                console.error('Error updating user progress:', data.message);
            }
        } catch (err) {
            console.error('Error during API call:', err);
        }
    };

    useEffect(() => {
        fetchPosts();
    }, []); 

    const fetchPosts = async () => {
        try {
            const response = await fetch('/api/fetch-posts', {
                method: 'GET',
                headers: { 'Content-Type': 'application/json' },
            });
            const data = await response.json();
            setPosts(data);
            const ids = data.data.map(item => item.id);
            fetchReplies(ids);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const handleReplySubmit = async (e, parentId) => {
        e.preventDefault();
        const token = localStorage.getItem('authToken');
        try {
            const response = await fetch('/api/reply', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ token, reply, parentId }), // Include parentId in the request body
            });
            const data = await response.json();    
            if (data.message === 'Reply successful!') {
                setReply('');
                fetchPosts();
                console.log('reply success');
            } else {
                console.error('Error adding reply:', data.message);
            }
        } catch (err) {
            console.error('Error during API call:', err);
        }
    };

    const fetchReplies = async (postId) => {
        console.log(postId);
        try {
            const response = await fetch(`/api/fetch-replies/${postId}`, {
                method: 'GET',
                headers: { 'Content-Type': 'application/json' },
            });
            const data = await response.json();
            setReplies(data);
        } catch (error) {
            console.error('Error fetching replies:', error);
        }
    };

    return (
        <main className='flex flex-row relative'>
            <DashboardNav />
            <section className='px-10 py-10 ms-64 w-2/3'>
                <form className="rounded-lg w-full bg-white border px-3 py-2 relative" onSubmit={handlePostSubmit}>
                    <input 
                        type="text" 
                        name="help-content" 
                        placeholder="Ask for help" 
                        className="border-0 bg-transparent focus:ring-0 w-full" 
                        value={help} 
                        onChange={(e) => setHelp(e.target.value)}
                    />
                    <button type="submit" className="absolute right-0 top-0 me-3 mt-2">
                        <img src={Send} alt="send" className="w-10"/>
                    </button>
                </form>
                <ul className="mt-10">
                    {posts.data && posts.data.length > 0 && (
                        <>
                            {posts.data.map((post) => (
                                <li key={post.id} className="p-6 bg-white border border-gray-200 rounded-lg mb-5">
                                    <div className='flex items-center'>
                                        <img src='https://www.svgrepo.com/show/492683/avatar-girl.svg' alt='Progress, rocket ship' className='w-16' />
                                        <div className="ms-3">
                                            <p className='font-bold text-lg'>{post.id}</p>
                                            <p className='font-bold text-lg'>{post.username}</p>
                                            <p className="text-sm text-gray-500">{post.created_at}</p>
                                        </div>
                                    </div>
                                    <p className="mt-3">{post.content}</p>
                                    {replies.data && replies.data.length > 0 && (
                                        <>
                                            {replies.data
                                                .filter((reply) => reply.post_id === post.id) // Filter replies for this post
                                                .map((reply) => (
                                                    <div key={reply.id} className="reply ms-16 mt-3">
                                                        <div className='flex items-center'>
                                                            <img src='https://www.svgrepo.com/show/492689/avatar-boy.svg' alt='Progress, rocket ship' className='w-16' />
                                                            <div className="ms-3">
                                                                <p className='font-bold text-lg'>{reply.id}</p>
                                                                <p className='font-bold text-lg'>{reply.username}</p>
                                                                <p className="text-sm text-gray-500">{reply.created_at}</p>
                                                            </div>
                                                        </div>
                                                        <p className="mt-3">{reply.content}</p>
                                                    </div>   
                                                ))}
                                        </>                          
                                    )}
                                    <form className="mt-5 relative" onSubmit={(e) => handleReplySubmit(e, post.id)}>
                                        <input 
                                            type="text" 
                                            name="reply-content" 
                                            placeholder="Write a reply" 
                                            className="border-0 focus:ring-0 w-full bg-gray-100 rounded-lg"
                                            value={reply} 
                                            onChange={(e) => setReply(e.target.value)}                     
                                        />
                                        <button type="submit"><img src={Send} alt="send" className="w-8 absolute right-3 top-1"/></button>
                                    </form>
                                </li>
                            ))}
                        </>
                    )}
                </ul>
            </section>
        </main>
    );
}

export default Community;
