import React from 'react';
import HeroImage from '../assets/images/player-hero.svg';

function Hero() {
    return (
        <section className='flex justify-between items-center'>
            <ul className='w-2/5'>
                <li><h1 className='text-6xl font-bold'>Learn SQL the Fun Way: Play Games, Earn Badges, Master Queries!</h1></li>
                <li><p>Turn SQL learning into an engaging adventure with interactive challenges, a rewarding badge system, and a supportive community.</p></li>
                <li><a href=''>Sign Up for Free</a></li>
            </ul>
            <img src={HeroImage} alt='' className='w-1/2' />
        </section>
    );
}

export default Hero;
