import React, { useState, useEffect } from 'react';
import DashboardNav from './UserDashboardNav';

function Settings() {
    const [userdata, setUserdata] = useState([]);

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('authToken');
            try {
                const response = await fetch(`/api/user-data`, {
                    headers: {
                      'Authorization': `Bearer ${token}`
                    }
                });                
                const data = await response.json();
                if (data.success) {
                    setUserdata(data.data);
                } else {
                    console.error('Error fetching user data:', data.message);
                }
            } catch (err) {
                console.error('Error during API call:', err);
            }
        };
        fetchUserData();  
    }, []);

    console.log(userdata);

    return (
        <main className='flex flex-row relative'>
            <DashboardNav />
            <section className='px-10 py-10 ms-64 w-2/3'>
                <h1 className='text-xl font-bold'>Settings</h1>
                <h2 className="text-2xl font-bold mt-10 border-b-2 pb-2">Edit Profile</h2>
                    <img src='https://www.svgrepo.com/show/492683/avatar-girl.svg' alt='Progress, rocket ship' className='w-24 mt-5' />
                    <form className="mt-5 w-1/2">
                        <ul>
                            <li className="mb-5">
                                <label htmlFor="username" className="block mb-2 font-medium">Username</label>
                                <input type="text" className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" placeholder='Enter your username here' />
                            </li>
                            <li className="mb-5">
                                <label htmlFor="username" className="block mb-2 font-medium">Email</label>
                                <input type="email" className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" placeholder='Enter your email here' />
                            </li>
                        </ul>
                        <button type="submit" className="custom-btn">Update</button>
                    </form>
                <h2 className="text-2xl font-bold mt-10 border-b-2 pb-2">Change Password</h2>
                <form className="my-5 w-1/2">
                    <ul>
                        <li className="mb-5">
                            <label htmlFor="username" className="block mb-2 font-medium">Old Password</label>
                            <input type="text" className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" placeholder='Enter your old password here' />
                        </li>
                        <li className="mb-5">
                            <label htmlFor="username" className="block mb-2 font-medium">New Password</label>
                            <input type="email" className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" placeholder='Enter your new password here' />
                        </li>
                        <li className="mb-5">
                            <label htmlFor="username" className="block mb-2 font-medium">Confirm New Password</label>
                            <input type="email" className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" placeholder='Confirm new password here' />
                        </li>
                    </ul>
                    <button type="submit" className="custom-btn">Update Password</button>
                </form>
            </section>
        </main>
    );
}

export default Settings;
