import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import AchievementFirstTime from '../../assets/images/achievement-first-time-user.svg';
import ProgressIcon from '../../assets/images/progress-icon.svg';
import LevelBanner from './LevelBanner';
import DashboardNav from './UserDashboardNav';

function UserDashboard() {
    const [currentLevel, setCurrentLevel] = useState('');
    const location = useLocation();

    useEffect(() => {
        const fetchCurrentLevel = async () => {
          const token = localStorage.getItem('authToken');
          try {
            const response = await fetch(`/api/user-data`, {
              headers: {
                'Authorization': `Bearer ${token}`
              }
            });
            const data = await response.json();
            if (data.success) {
                setCurrentLevel(data.user.current_level); // Update state with current level
            } else {
                console.error('Error fetching current level:', data.message);
            }
          } catch (err) {
            console.error('Error during API call:', err);
          }
        };
        fetchCurrentLevel();
      }, []);
      


    return (
        <main className='flex flex-row relative'>
            <DashboardNav />
            <section className='px-10 py-10 w-full'>
                <div className='relative flex flex-col justify-between'>
                    <section className='w-1/2 absolute ms-64'>
                        <h1 className='text-xl font-bold'>Welcome back, User!</h1>
                        {/* <LevelBanner level="easy" title="Baby Steps" description="Master the fundamentals and build a strong foundation."  />
                        <LevelBanner level="moderate" title="Challenge Accepted!" description="Put your skills to the test with increasing difficulty."  />
                        <LevelBanner level="hard" title="Mastermind in the Making" description="Hone your skills and become a true expert."  /> */}
                        
                    </section>
                    <section className='w-1/3 fixed right-0'>
                        <ul className='flex flex-col gap-4'>
                            <li className="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg">
                                <h2 className="mb-2 text-xl font-bold text-gray-900">Achievements</h2>
                                <div className='flex justify-between items-center gap-4 mt-3'>
                                    <img src={AchievementFirstTime} alt='Achivements picture' className='size-16' />
                                    <ul>
                                        <li className='font-bold text-primary text-lg'>Fresh Start</li>
                                        <li className='text-sm text-gray-500'>Welcome to the community! Get ready to embark on a rewarding journey.</li>
                                    </ul>
                                </div>
                            </li>
                            <li className="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg">
                                <h2 className="mb-2 text-xl font-bold">Progress</h2>
                                <div className='flex mt-3'>
                                    <img src={ProgressIcon} alt='Progress, rocket ship' className='w-16' />
                                    <div className='relative w-full'>
                                        <h3 className='font-bold text-primary text-lg mb-2'>Easy Level</h3>
                                        <span className='absolute top-7 bg-ternary text-secondary font-bold text-lg size-8 rounded-full flex justify-center items-center border-4 border-white'>1</span>
                                        <div className="w-full h-4 mb-4 bg-gray-200 rounded-full">
                                            <div className="h-4 bg-primary rounded-full w-1/2"></div>
                                        </div>
                                        <span className='absolute top-7 right-0 bg-ternary text-secondary font-bold text-lg size-8 rounded-full flex justify-center items-center border-4 border-white'>2</span>
                                    </div>
                                </div>
                                <a href='/Game' className='custom-btn w-full mt-3'>Continue Learning</a>
                            </li>
                        </ul>
                    </section>            
                </div>
            </section>
        </main>
    );  
}

export default UserDashboard;
