import React from "react";
import FreshStart from '../../assets/images/achievement-first-time-user.svg';

function AwardModal({ isOpen, award = null }) {
    if (!isOpen || !award) return null;

    return (
        <div id="popup-modal" tabindex="-1" className="hidden overflow-y-auto overflow-x-hidden fixed top-0 right-0 left-0 z-50 justify-center items-center w-full md:inset-0 h-[calc(100%-1rem)] max-h-full">
            <div className="relative p-4 w-full max-w-md max-h-full">
                <div className="relative bg-white rounded-lg shadow dark:bg-gray-700">
                    <div className="p-4 md:p-5 text-center">
                        <p className="mb-5 text-lg font-normal text-gray-500 dark:text-gray-400">You just earned an achievement!</p>
                        <img src={FreshStart} alt="target" className="h-28"/>
                        <h2>Fresh Start</h2>
                        <p>Welcome to the community! You've unlocked the "Fresh Start" badge. Get ready to embark on a rewarding journey.</p>
                        <button data-modal-hide="popup-modal" type="button" className="custom-btn">Continue</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AwardModal;
