import React from 'react';
import { useDroppable } from '@dnd-kit/core';

const Droppable = ({ items, setQueryItems, queryItems }) => {
    const { setNodeRef } = useDroppable({
        id: "droppable"
    });

    const handleRemoveItem = (itemToRemove) => {
        const updatedItems = queryItems.filter(item => item !== itemToRemove);
        setQueryItems(updatedItems);
		console.log('clicked');
    };

    return (
        <ul ref={setNodeRef} className='flex border-b bg-white h-14 my-3 pt-1'>
            {items.map((item, idx) => (
                <li key={`${item}-${idx}`} className='py-3 px-5 mb-1 me-1 font-semibold bg-white rounded-lg border border-gray-300 font-mono'>
                    <span onClick={() => handleRemoveItem(item)}>{item}</span>
                </li>
            ))}
        </ul>
    );
};

export default Droppable;
