import React, { useState, useEffect } from 'react';
import DashboardNav from './UserDashboardNav';

function Settings() {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [oldPassword, setOldPassword] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('');

    const [oldPasswordError, setOldPasswordError] = useState(null);
    const [newPasswordError, setNewPasswordError] = useState(null);
    const [confirmPasswordError, setConfirmPasswordError] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            const token = localStorage.getItem('authToken');
            try {
                const response = await fetch(`/api/user-data`, {
                    headers: {
                      'Authorization': `Bearer ${token}`
                    }
                });                
                const data = await response.json();
                if (data.success) {
                    setUsername(data.user.username);
                    setEmail(data.user.email);
                } else {
                    console.error('Error fetching user data:', data.message);
                }
            } catch (err) {
                console.error('Error during API call:', err);
            }
        };
        fetchUserData();  
    }, []);
    

    const handleProfileUpdate = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('authToken');
        try {
            const response = await fetch('/api/update-profile ', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ token, username, email }),
            });
            const data = await response.json();    
            if (data.success) {
                console.log('update success');
            } else {
                console.error('Error updating user:', data.message);
            }
        } catch (err) {
            console.error('Error during API call:', err);
        }    
    };

    return (
        <main className='flex flex-row relative'>
            <DashboardNav />
            <section className='px-10 py-10 ms-64 w-2/3'>
                <h1 className='text-xl font-bold'>Settings</h1>
                <h2 className="text-2xl font-bold mt-10 border-b-2 pb-2">Edit Profile</h2>
                    <img src='https://www.svgrepo.com/show/492683/avatar-girl.svg' alt='Progress, rocket ship' className='w-24 mt-5' />
                    <form className="mt-5 w-1/2" onSubmit={handleProfileUpdate}>
                        <ul>
                            <li className="mb-5">
                                <label htmlFor="username" className="block mb-2 font-medium">Username</label>
                                <input 
                                    type="text" 
                                    className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" 
                                    placeholder='Enter your username here' 
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                />
                            </li>
                            <li className="mb-5">
                                <label htmlFor="username" className="block mb-2 font-medium">Email</label>
                                <input 
                                    type="email" 
                                    className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" 
                                    placeholder='Enter your email here' 
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                />
                            </li>
                        </ul>
                        <button type="submit" className="custom-btn">Update</button>
                    </form>
                <h2 className="text-2xl font-bold mt-10 border-b-2 pb-2">Change Password</h2>
                <form className="my-5 w-1/2">
                    <ul>
                        <li className="mb-5">
                            <label htmlFor="username" className="block mb-2 font-medium">Old Password</label>
                            <input 
                                type="password" 
                                className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" 
                                placeholder='Enter your old password here' 
                                value={oldPassword}
                                onChange={(e) => setOldPassword(e.target.value)}
                            />
                        </li>
                        <li className="mb-5">
                            <label htmlFor="username" className="block mb-2 font-medium">New Password</label>
                            <input 
                                type="password" 
                                className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" 
                                placeholder='Enter your old password here' 
                                value={newPassword}
                                onChange={(e) => setNewPassword(e.target.value)}
                            />
                        </li>
                        <li className="mb-5">
                            <label htmlFor="username" className="block mb-2 font-medium">Confirm New Password</label>
                            <input 
                                type="password" 
                                className="text-sm bg-gray-50 border border-gray-300 rounded-lg focus:border-primary block w-full p-2.5" 
                                placeholder='Enter your old password here' 
                                value={confirmNewPassword}
                                onChange={(e) => setConfirmNewPassword(e.target.value)}
                            />
                        </li>
                    </ul>
                    <button type="submit" className="custom-btn">Update Password</button>
                </form>
            </section>
        </main>
    );
}

export default Settings;
