import React, { useState, useEffect } from 'react';
import Send from '../../assets/images/send.svg';
import DashboardNav from './UserDashboardNav';

function Community() {
    const [help, setHelp] = useState('');
    const [posts, setPosts] = useState([]);
    const [replies, setReplies] = useState({});

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('authToken');
        try {
            const response = await fetch('/api/post', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ token, help }),
            });
            const data = await response.json();    
            if (data.message === 'Post successful!') {
                console.log("successful");
                setHelp('');
            } else {
                console.error('Error updating user progress:', data.message);
            }
        } catch (err) {
            console.error('Error during API call:', err);
        }
    };

    // const fetchReplies = async (postId) => {
    //     try {
    //         const response = await fetch(`/api/replies/${postId}`);
    //         const data = await response.json();
    //         setReplies(prevReplies => ({ ...prevReplies, [postId]: data.replies }));
    //     } catch (error) {
    //         console.error('Error fetching replies:', error);
    //     }
    // };

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch('/api/fetch-posts', {
                    method: 'GET',
                    headers: { 'Content-Type': 'application/json' },
                });
                const data = await response.json();
                const postsWithReplies = data.data.map(post => ({ ...post, replies: [] })); // Initialize empty replies array for each post
                postsWithReplies.forEach(post => fetchReplies(post.id)); // Fetch replies for each post
                setPosts(postsWithReplies);
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };
        fetchPosts();
    }, [posts]); 

    return (
        <main className='flex flex-row relative'>
            <DashboardNav />
            <section className='px-10 py-10 ms-64 w-2/3'>
                <form className="rounded-lg w-full bg-white border px-3 py-2 relative" onSubmit={handleSubmit}>
                    <input 
                        type="text" 
                        name="help-content" 
                        placeholder="Ask for help" 
                        className="border-0 bg-transparent focus:ring-0 w-full" 
                        value={help} 
                        onChange={(e) => setHelp(e.target.value)}
                    />
                    <button type="submit" className="absolute right-0 top-0 me-3 mt-2">
                        <img src={Send} alt="send" className="w-10"/>
                    </button>
                </form>
                <ul className="mt-10">
                    {posts.data && posts.data.length > 0 && (
                        <>
                            {posts.data.map((post) => (
                                <li key={post.id} className="p-6 bg-white border border-gray-200 rounded-lg mb-5">
                                    <div className='flex items-center'>
                                        <img src='https://www.svgrepo.com/show/492683/avatar-girl.svg' alt='Progress, rocket ship' className='w-16' />
                                        <div className="ms-3">
                                            <p className='font-bold text-lg'>{post.username}</p>
                                            <p className="text-sm text-gray-500">{post.created_at}</p>
                                        </div>
                                    </div>
                                    <p className="mt-3">{post.content}</p>
                                    <div className="reply ms-16 mt-3">
                                        <div className='flex items-center'>
                                            <img src='https://www.svgrepo.com/show/492689/avatar-boy.svg' alt='Progress, rocket ship' className='w-16' />
                                            <div className="ms-3">
                                                <p className='font-bold text-lg'>Jose Rizal</p>
                                                <p className="text-sm text-gray-500">Just now</p>
                                            </div>
                                        </div>
                                        <p className="mt-3">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
                                    </div>
                                    <form className="mt-5 relative">
                                        <input type="text" name="help-content" placeholder="Ask for help" className="border-0 focus:ring-0 w-full bg-gray-100 rounded-lg" />
                                        <button type="submit"><img src={Send} alt="send" className="w-8 absolute right-3 top-1"/></button>
                                    </form>
                                </li>
                            ))}
                        </>
                    )}
                </ul>
            </section>
        </main>
    );
}

export default Community;
